//   //When clicking start, starte counting
// }

function App() {
  const [second, setSecond] = useLocalStorageState("second");
  const [minute, setMinute] = useLocalStorageState("minute");
  const [hour, setHour] = useLocalStorageState("hour");

  const [state, dispatch] = useReducer(reducer, initialState);
  const initialState = { second, minute, hour };
  function reducer(state, action) {
    switch (action.type) {
      case "stop":
        return initialState;
      case "start":
        return initialState;
      default:
        return initialState;
    }
  }

  console.log(initialState);
  return (
    <div className="App">
      <header>Coder Timer</header>
      <main>
        <div className="container">
          <div className="timer">
            {hour} : {minute} : {second}
          </div>
          <div className="func-btn">
            <button>Stop</button>
            <button>Start</button>
            <button>Reset</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
