import React, { useState, useEffect, useContext } from "react";
import { TimerContext } from "../App";

function useHistoryRecord(key, defaultValue = []) {
  const hour = useContext(TimerContext);
  const minute = useContext(TimerContext);
  const second = useContext(TimerContext);
  const [state, setState] = useState(
    () => window.localStorage.getItem(key) || defaultValue
  );

  useEffect(() => {
    window.localStorage.setItem(key, state);
  }, [key, state]);

  const handleHistory = () => {
    setState([...state, { hour, minute, second }]);
  };
  return [state, handleHistory];
}

export default useHistoryRecord;
