import { useEffect, useState } from "react";
import "./App.css";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [second, setSecond] = useLocalStorage("second", "00");
  const [minute, setMinute] = useLocalStorage("minute");
  const [hour, setHour] = useLocalStorage("hour");

  const handleClick = (e) => {
    setSecond((second) =>
      (Number(second) + 1).toString.length < 2
        ? `'0' + ${Number(second) + 1}`
        : (Number(second) + 1).toString
    );
  };

  return (
    <div className="App">
      <header>Coder Timer</header>
      <main>
        <div className="container">
          <div className="timer">
            {hour} : {minute} : {second}
          </div>
          <div className="func-btn">
            <button name="stop">Stop</button>
            <button name="start" onClick={handleClick}>
              Start
            </button>
            <button name="reset">Reset</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
