import "./App.css";
import { useReducer, useState, useEffect } from "react";
import useLocalStorageState from "./components/useLocalStorageState";

// function updateTimer() {
//   //When clicking start, starte counting
// }

function App() {
  const [second, setSecond] = useLocalStorageState("second");
  const [minute, setMinute] = useLocalStorageState("minute");
  const [hour, setHour] = useLocalStorageState("hour");
  const [isClickedStart, setIsClickedStart] = useState(false);
  const [isClickedStop, setIsClickedStop] = useState(false);

  const handleClick = (e) => {
    const name = e.target.name;
    if (name == "start") return setIsClickedStart((prevState) => !prevState);
    if (name == "stop") return setIsClickedStop((prevState) => !prevState);
  };
  // useEffect(() => {

  // }, [isClicked, second, minute, hour])

  return (
    <div className="App">
      <header>Coder Timer</header>
      <main>
        <div className="container">
          <div className="timer">
            {hour} : {minute} : {second}
          </div>
          <div className="func-btn">
            <button name="stop" onClick={handleClick}>
              Stop
            </button>
            <button name="start" onClick={handleClick}>
              Start
            </button>
            <button name="reset" onClick={handleClick}>
              Reset
            </button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
