import "./App.css";
import { useReducer, useState } from "react";
import useLocalStorageState from "./components/useLocalStorageState";

// function updateTimer() {
//   //When clicking start, starte counting
// }

function App() {
  const [second, setSecond] = useLocalStorageState("second");
  const [minute, setMinute] = useLocalStorageState("minute");
  const [hour, setHour] = useLocalStorageState("hour");
  const initialState = { second, minute, hour };

  const [state, dispatch] = useReducer(reducer, initialState);
  function reducer(state, action) {
    switch (action.type) {
      case "stop":
        return initialState;
      case "start":
        return initialState;
      default:
        return initialState;
    }
  }

  function updateTimer() {
    setSecond(setInterval(Number(second) + 1), 1000);
  }

  return (
    <div className="App">
      <header>Coder Timer</header>
      <main>
        <div className="container">
          <div className="timer">
            {hour} : {minute} : {second}
          </div>
          <div className="func-btn">
            <button onClick={() => dispatch({ type: "stop" })}>Stop</button>
            <button onClick={() => dispatch({ type: "start" })}>Start</button>
            <button onClick={() => dispatch({ type: "reset" })}>Reset</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
