import React, { useReducer } from "react";

const initialValue = {
  hour: "00",
  minute: "00",
  second: "00",
};

const timerReducer = (state, action) => {
  switch (action.type) {
    case "stop":
      return initialValue;
    case "stop":
      return initialValue;
    case "stop":
      return initialValue;
  }
};

function App() {
  const [state, dispatch] = useReducer(timerReducer, initialValue);
  const { hour, minute, second } = state;

  return (
    <div>
      <h1>Stopwatch</h1>
      <div className="timer">
        <div className="hour">{state.hour}</div>
        <div className="button">
          <button>Stop</button>
          <button>Start</button>
          <button>Restart</button>
        </div>
      </div>
    </div>
  );
}

export default App;
