import "./App.css";
import { useReducer, useEffect } from "react";

const initialState = { second: 0, minute: 0, hour: 0 };

function reducer(state, action) {
  switch (action.type) {
    case "stop":
      return { count: 0 };
    case "start":
      return { count: 0 };
    default:
      return { count: 0 };
  }
}

function App() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const [timer, setTimer] = useState({
    second: "00",
  });
  const { count } = state;

  const time = setInterval(state.count, 1);

  return (
    <div className="App">
      <header>Coder Timer</header>
      <main>
        <div className="container">
          <div className="timer">Hour showing</div>
          <div className="func-btn">
            <button onClick={() => dispatch({ type: "stop" })}>Stop</button>
            <button onClick={() => dispatch({ type: "start" })}>Start</button>
            <button onClick={() => dispatch({ type: "reset" })}>Reset</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
