import { useEffect, useState } from "react";
import "./App.css";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [second, setSecond] = useLocalStorage("second");
  const [minute, setMinute] = useLocalStorage("minute");
  const [hour, setHour] = useLocalStorage("hour");
  const [isClicked, setIsClicked] = useState(false);

  const handleClick = (e) => {
    setIsClicked((prevState) => !prevState);
    // setSecond((second) => {
    //   let sec = Number(second) + 1;
    //   if (sec < 10) return `0${sec}`;
    //   if (sec < 60) return sec.toString();
    // });
  };

  useEffect(() => {
    const interval = setInterval(() => setSecond(Number(second) + 1), 1000);
    if (second == 60) {
      setMinute(Number(minute) + 1);
    }
    if (hour == 60) setHour(Number(hour) + 1);
    return () => clearInterval(interval);
  }, [second]);

  return (
    <div className="App">
      <header>Coder Timer</header>
      <main>
        <div className="container">
          <div className="timer">
            {hour} : {minute} : {second}
          </div>
          <div className="func-btn">
            <button name="stop">Stop</button>
            <button name="start" onClick={handleClick}>
              Start
            </button>
            <button name="reset">Reset</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
