import "./App.css";
import { useReducer, useEffect } from "react";

const initialState = { second: "00", minute: "00", hour: "00" };

function reducer(state, action) {
  switch (action.type) {
    case "stop":
      return initialState;
    case "start":
      return initialState;
    default:
      return initialState;
  }
}

let second = setInterval(
  () => (initialState = Number(initialState.second) + 1),
  1000
);

function App() {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div className="App">
      <header>Coder Timer</header>
      <main>
        <div className="container">
          <div className="timer">
            {state.hour} : {state.minute} : {second}
          </div>
          <div className="func-btn">
            <button onClick={() => dispatch({ type: "stop" })}>Stop</button>
            <button onClick={() => dispatch({ type: "start" })}>Start</button>
            <button onClick={() => dispatch({ type: "reset" })}>Reset</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
