import { Button, Typography } from "@mui/material";
import { Box } from "@mui/system";
import React, { useState, useEffect } from "react";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [hour, setHour] = useLocalStorage("hour");
  const [minute, setMinute] = useLocalStorage("minute");
  const [second, setSecond] = useLocalStorage("second");
  const [isClick, setIsClick] = useState(false);
  const [history, setHistory] = useState([]);

  const handleRestart = (e) => {
    setHour("0");
    setMinute("0");
    setSecond("0");
    setIsClick(false);
  };

  const handleHistory = () => {
    setHistory([...history, { hour, minute, second }]);
  };

  useEffect(() => {
    if (isClick) {
      const interval = setInterval(() => setSecond(+second + 1), 1000);
      if (second === 60) {
        setMinute(+minute + 1);
        setSecond(0);
      }
      if (minute === 60) {
        setHour(+hour + 1);
        setMinute(0);
      }

      return () => {
        clearInterval(interval);
      };
    }
  });

  return (
    <div>
      <Typography>Stopwatch</Typography>

      <div className="timer">
        <Box>
          <Typography variant="h1">
            {hour} : {minute} : {second}
          </Typography>
        </Box>

        <Box>
          <Button variant="contained" onClick={() => setIsClick(false)}>
            Stop
          </Button>
          <Button
            variant="contained"
            onClick={() => setIsClick((prevState) => !prevState)}
          >
            Start
          </Button>

          <Button variant="contained" onClick={handleRestart}>
            Restart
          </Button>
          <Button variant="contained" onClick={handleHistory}>
            Record
          </Button>
        </Box>
        <div>
          <h3>History</h3>
          <div>
            {history.map((record) => (
              <div id={Date.now()}>
                {record.hour} : {record.minute} : {record.second}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
