import React, { useReducer, useState, useEffect } from "react";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [second, setSecond] = useLocalStorage("second");
  const [minute, setMinute] = useLocalStorage("minute");
  const [hour, setHour] = useLocalStorage("hour");
  const [clickStart, setClickStart] = useState(false);

  const handleClick = (e) => {
    setSecond(Number(second + 1));
  };

  useEffect(() => {
    const interval = () => {
      setInterval(() => Number(second) + 1, 1000);
    };

    return () => {
      clearInterval(interval);
    };
  }, [setSecond, second]);

  return (
    <div className="App">
      <header>Coder Timer</header>
      <div className="container">
        <div className="timer">
          {hour} : {minute} : {second}
        </div>
        <div className="func-btn">
          <button name="stop">Stop</button>
          <button name="start" onClick={handleClick}>
            Start
          </button>
          <button name="reset">Reset</button>
        </div>
      </div>
    </div>
  );
}

export default App;
