import React, { useReducer, useState } from "react";
import useLocalStorage from "./hooks/useLocalStorage";
const initialState = { second: "00", minute: "00", hour: "00" };

function reducer({ state, action }) {
  switch (action.type) {
    case "restart":
      return initialState;
    case "start":
      return initialState;
    case "stop":
      return initialState;
  }
}

function App() {
  const [second, setSecond] = useLocalStorage("second");
  const [minute, setMinute] = useLocalStorage("minute");
  const [hour, setHour] = useLocalStorage("hour");
  const [state, dispatch] = useReducer(reducer, initialState);
  console.log(state);

  return (
    <div className="App">
      <header>Coder Timer</header>
      <div className="container">
        <div className="timer">
          {hour} : {minute} : {second}
        </div>
        <div className="func-btn">
          <button name="stop">Stop</button>
          <button name="start">Start</button>
          <button name="reset">Reset</button>
        </div>
      </div>
    </div>
  );
}

export default App;
