import "./App.css";
import { useReducer, useState, useEffect } from "react";
import useLocalStorageState from "./components/useLocalStorageState";

// function updateTimer() {
//   //When clicking start, starte counting
// }

function App() {
  const [second, setSecond] = useLocalStorageState("second");
  const [minute, setMinute] = useLocalStorageState("minute");
  const [hour, setHour] = useLocalStorageState("hour");
  const [isClicked, setIsClicked] = useState(false);

  useEffect(() => {
    //Khi start duoc click, thi so gio update theo
    setSecond(setInterval((second) => Number(second) + 1, 1000));
  }, [isClicked]);

  return (
    <div className="App">
      <header>Coder Timer</header>
      <main>
        <div className="container">
          <div className="timer">
            {hour} : {minute} : {second}
          </div>
          <div className="func-btn">
            <button name="stop">Stop</button>
            <button name="start">Start</button>
            <button name="reset">Reset</button>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
