import React, { useReducer, useState, useEffect } from "react";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [hour, setHour] = useLocalStorage("hour");
  const [minute, setMinute] = useLocalStorage("minute");
  const [second, setSecond] = useLocalStorage("second");
  const [isStopped, setIsStopped] = useState(false);
  const [isStarted, setIsStarted] = useState(false);

  return (
    <div>
      <h1>Stopwatch</h1>
      <div className="timer">
        <div className="hour">
          {hour} : {minute} : {second}
        </div>
        <div className="button">
          <button>Stop</button>
          <button onClick={(e) => dispatch({ type: "start" })}>Start</button>
          <button onClick={(e) => dispatch({ type: "restart" })}>
            Restart
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
