import React, { useReducer, useState, useEffect } from "react";

const initialValue = {
  hour: "00",
  minute: "00",
  second: "00",
};

const timerReducer = (state, action) => {
  switch (action.type) {
    case "stop":
      return state;
    case "start":
      return { ...state, second: setInterval(() => Number(state.second) + 1) };
    case "restart":
      return initialValue;
    default:
      throw new Error();
  }
};

function App() {
  const [state, dispatch] = useReducer(timerReducer, initialValue);
  const { hour, minute, second } = state;
  const [isStopped, setIsStopped] = useState(false);
  const [isStarted, setIsStarted] = useState(false);

  return (
    <div>
      <h1>Stopwatch</h1>
      <div className="timer">
        <div className="hour">
          {state.hour} : {state.minute} : {state.second}
        </div>
        <div className="button">
          <button>Stop</button>
          <button onClick={() => dispatch({ type: "start" })}>Start</button>
          <button onClick={() => dispatch({ type: "restart" })}>Restart</button>
        </div>
      </div>
    </div>
  );
}

export default App;
