import {
  Button,
  Card,
  CardActions,
  CardContent,
  Paper,
  Typography,
} from "@mui/material";
import { Box } from "@mui/system";
import React, { useState, useEffect } from "react";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [hour, setHour] = useLocalStorage("hour");
  const [minute, setMinute] = useLocalStorage("minute");
  const [second, setSecond] = useLocalStorage("second");
  const [isClick, setIsClick] = useState(false);
  const [history, setHistory] = useState([]);

  const handleRestart = (e) => {
    setHour("0");
    setMinute("0");
    setSecond("0");
    setIsClick(false);
  };

  const handleHistory = () => {
    setHistory([...history, { hour, minute, second }]);
  };

  useEffect(() => {
    if (isClick) {
      const interval = setInterval(() => setSecond(+second + 1), 1000);
      if (second === 60) {
        setMinute(+minute + 1);
        setSecond(0);
      }
      if (minute === 60) {
        setHour(+hour + 1);
        setMinute(0);
      }

      return () => {
        clearInterval(interval);
      };
    }
  });

  return (
    <Box
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="center"
    >
      <Card>
        <CardContent>
          <Typography variant="h3">Stopwatch</Typography>
        </CardContent>

        <CardContent sx={{ display: "flex" }}>
          <Typography variant="h2">
            {hour} : {minute} : {second}
          </Typography>
        </CardContent>

        <CardActions>
          <Button variant="contained" onClick={() => setIsClick(false)}>
            Stop
          </Button>
          <Button
            variant="contained"
            onClick={() => setIsClick((prevState) => !prevState)}
          >
            Start
          </Button>
          <Button variant="contained" onClick={handleRestart}>
            Restart
          </Button>
          <Button variant="contained" onClick={handleHistory}>
            Record
          </Button>
        </CardActions>
      </Card>

      <Card>
        <Typography variant="h5" fontWeight="bold">
          History
        </Typography>

        {history.map((record) => (
          <Paper elevation={3} key={Date.now()}>
            {record.hour} : {record.minute} : {record.second}
          </Paper>
        ))}
      </Card>
    </Box>
  );
}

export default App;
