import React, { useReducer, useState } from "react";
import useLocalStorage from "./hooks/useLocalStorage";

// const initialState = {
//   second: "00",
//   minute: "00",
//   hour: "00",
// };

const reducer = (state, action) => {
  switch (action.type) {
    case "reset":
      return initialState;
    case "start":
      return initialState;
    case "stop":
      return initialState;
    default:
      throw new Error();
  }
};

function App() {
  const [second, setSecond] = useLocalStorage("second");
  const [minute, setMinute] = useLocalStorage("minute");
  const [hour, setHour] = useLocalStorage("hour");
  const initialState = { second, minute, hour };
  console.log(initialState);

  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div className="App">
      <header>Coder Timer</header>
      <div className="container">
        <div className="timer">
          {state.hour} : {state.minute} : {state.second}
        </div>
        <div className="func-btn">
          <button name="stop">Stop</button>
          <button name="start">Start</button>
          <button
            name="reset"
            onClick={() => dispatch({ type: "reset", payload: {} })}
          >
            Reset
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
