import React, { useState, useEffect } from "react";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [hour, setHour] = useLocalStorage("hour");
  const [minute, setMinute] = useLocalStorage("minute");
  const [second, setSecond] = useLocalStorage("second");

  const [isClick, setIsClick] = useState(false);

  const handleRestart = () => {
    setHour("00");
    setMinute("00");
    setSecond("00");
    setIsClick(false);
  };

  useEffect(() => {
    if (isClick) {
      const interval = setInterval(() => setSecond(+second + 1), 1000);
      if (second === 60) {
        setMinute(+minute + 1);
        setSecond(0);
      }
      if (minute === 60) {
        setHour(+hour + 1);
        setMinute(0);
      }

      return () => {
        clearInterval(interval);
      };
    }
  });

  return (
    <div>
      <h1>Stopwatch</h1>
      <div className="timer">
        <div className="hour">
          {hour} : {minute} : {second}
        </div>
        <div className="button">
          <button onClick={() => setIsClick(false)}>Stop</button>
          <button onClick={() => setIsClick(true)}>Start</button>
          <button onClick={handleRestart}>Restart</button>
        </div>
      </div>
    </div>
  );
}

export default App;
