import React, { useReducer, useState } from "react";
import useLocalStorage from "./hooks/useLocalStorage";

function App() {
  const [second, setSecond] = useLocalStorage("second", "04");
  const [minute, setMinute] = useLocalStorage("minute");
  const [hour, setHour] = useLocalStorage("hour");
  const [state, dispatch] = useReducer(reducer, initialState);
  const initialState = { hour, minute, second };
  console.log(initialState);

  function reducer({ state, action }) {
    switch (action.type) {
      case "reset":
        return { ...initialState };
      case "start":
        return initialState;
      case "stop":
        return initialState;
      default:
        throw Error;
    }
  }

  return (
    <div className="App">
      <header>Coder Timer</header>
      <div className="container">
        <div className="timer">
          {hour} : {minute} : {second}
        </div>
        <div className="func-btn">
          <button name="stop">Stop</button>
          <button name="start">Start</button>
          <button name="reset" dispatch={{ type: "reset" }}>
            Reset
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
